mapper.select:
SELECT SUM(payment_amount) FROM biz_payment_order


        AND payment_no LIKE CONCAT('%', #{paymentNo}, '%')


        AND project_id = #{projectId}


        AND project_type = #{projectType}


        AND project_name LIKE CONCAT('%', #{projectName}, '%')


        AND payee_type = #{payeeType}


        AND payee_id = #{payeeId}


        AND payee_name LIKE CONCAT('%', #{payeeName}, '%')


        AND payment_time BETWEEN #{paymentTime.begin} AND #{paymentTime.end}


        AND payment_method = #{paymentMethod}


        AND is_invoiced = #{isInvoiced}


        AND create_time BETWEEN #{createTime.begin} AND #{createTime.end}

    and deleted = false
