mapper.select:
SELECT SUM(receipt_amount) FROM biz_receipt_order


        AND receipt_no LIKE CONCAT('%', #{receiptNo}, '%')


        AND receipt_type = #{receiptType}


        AND project_type = #{projectType}


        AND project_scattered_type = #{projectScatteredType}


        AND project_no LIKE CONCAT('%', #{projectNo}, '%')


        AND fiscal_year = #{fiscalYear}


        AND project_name LIKE CONCAT('%', #{projectName}, '%')


        AND payer_name LIKE CONCAT('%', #{payerName}, '%')


        AND receipt_date BETWEEN #{receiptDate.begin} AND #{receiptDate.end}


        AND receipt_method = #{receiptMethod}


        AND is_invoiced = #{isInvoiced}


        AND create_time BETWEEN #{createTime.begin} AND #{createTime.end}

    and deleted = false
